					$this->collCountryI18ns = CountryI18nPeer::doSelect($criteria, $con);
				}
			}
		}
		$this->lastCountryI18nCriteria = $criteria;
		return $this->collCountryI18ns;
	}

	/**
	 * Returns the number of related CountryI18n objects.
	 *
	 * @param      Criteria $criteria
	 * @param      boolean $distinct
	 * @param      PropelPDO $con
	 * @return     int Count of related CountryI18n objects.
	 * @throws     PropelException
	 */
	public function countCountryI18ns(Criteria $criteria = null, $distinct = false, PropelPDO $con = null)
	{
		if ($criteria === null) {
			$criteria = new Criteria(CountryPeer::DATABASE_NAME);
		} else {
			$criteria = clone $criteria;
		}

		if ($distinct) {
			$criteria->setDistinct();
		}

		$count = null;

		if ($this->collCountryI18ns === null) {
			if ($this->isNew()) {
				$count = 0;
			} else {

				$criteria->add(CountryI18nPeer::ID, $this->id);

				$count = CountryI18nPeer::doCount($criteria, false, $con);
			}
		} else {
			// criteria has no effect for a new object
			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return count of the collection.


				$criteria->add(CountryI18nPeer::ID, $this->id);

				if (!isset($this->lastCountryI18nCriteria) || !$this->lastCountryI18nCriteria->equals($criteria)) {
					$count = CountryI18nPeer::doCount($criteria, false, $con);
				} else {
					$count = count($this->collCountryI18ns);
				}
			} else {
				$count = count($this->collCountryI18ns);
			}
		}
		return $count;
	}

	/**
	 * Method called to associate a CountryI18n object to this object
	 * through the CountryI18n foreign key attribute.
	 *
	 * @param      CountryI18n $l CountryI18n
	 * @return     void
	 * @throws     PropelException
	 */
	public function addCountryI18n(CountryI18n $l)
	{
		if ($this->collCountryI18ns === null) {
			$this->initCountryI18ns();
		}
		if (!in_array($l, $this->collCountryI18ns, true)) { // only add it if the **same** object is not already associated
			array_push($this->collCountryI18ns, $l);
			$l->setCountry($this);
		}
	}

	/**
	 * Resets all collections of referencing foreign keys.
	 *
	 * This method is a user-space workaround for PHP's inability to garbage collect objects
	 * with circular references.  This is currently necessary when using Propel in certain
	 * daemon or large-volumne/high-memory operations.
	 *
	 * @param      boolean $deep Whether to also clear the references on all associated objects.
	 */
	public function clearAllReferences($deep = false)
	{
		if ($deep) {
			if ($this->collCustomers) {
				foreach ((array) $this->collCustomers as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collUsers) {
				foreach ((array) $this->collUsers as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collCountryI18ns) {
				foreach ((array) $this->collCountryI18ns as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collCustomers = null;
		$this->collUsers = null;
		$this->collCountryI18ns = null;
			$this->aContinent = null;
	}

	// symfony_behaviors behavior
	
	/**
	 * Calls methods defined via {@link sfMixer}.
	 */
	public function __call($method, $arguments)
	{
	  if (!$callable = sfMixer::getCallable('BaseCountry:'.$method))
	  {
	    throw new sfException(sprintf('Call to undefined method BaseCountry::%s', $method));
	  }
	
	  array_unshift($arguments, $this);
	
	  return call_user_func_array($callable, $arguments);
	}

	// symfony_i18n behavior
	
	/**
	 * Returns the culture.
	 *
	 * @return string The culture
	 */
	public function getCulture()
	{
	  return $this->culture;
	}
	
	/**
	 * Sets the culture.
	 *
	 * @param string  The culture to set
	 *
	 * @return Country
	 */
	public function setCulture($culture)
	{
	  $this->culture = $culture;
	  return $this;
	}
	
	/**
	 * Returns the "title" value from the current {@link CountryI18n}.
	 */
	public function getTitle($culture = null)
	{
	  return $this->getCurrentCountryI18n($culture)->getTitle();
	}
	
	/**
	 * Sets the "title" value of the current {@link CountryI18n}.
	 *
	 * @return Country
	 */
	public function setTitle($value, $culture = null)
	{
	  $this->getCurrentCountryI18n($culture)->setTitle($value);
	  return $this;
	}
	
	/**
	 * Returns the current translation.
	 *
	 * @return CountryI18n
	 */
	public function getCurrentCountryI18n($culture = null)
	{
	  if (null === $culture)
	  {
	    $culture = null === $this->culture ? sfPropel::getDefaultCulture() : $this->culture;
	  }
	
	  if (!isset($this->current_i18n[$culture]))
	  {
	    $object = $this->isNew() ? null : CountryI18nPeer::retrieveByPK($this->getPrimaryKey(), $culture);
	    if ($object)
	    {
	      $this->setCountryI18nForCulture($object, $culture);
	    }
	    else
	    {
	      $this->setCountryI18nForCulture(new CountryI18n(), $culture);
	      $this->current_i18n[$culture]->setCulture($culture);
	    }
	  }
	
	  return $this->current_i18n[$culture];
	}
	
	/**
	 * Sets the translation object for a culture.
	 */
	public function setCountryI18nForCulture(CountryI18n $object, $culture)
	{
	  $this->current_i18n[$culture] = $object;
	  $this->addCountryI18n($object);
	}

} // BaseCountry
