	/**
	 * Method perform an INSERT on the database, given a Customer or Criteria object.
	 *
	 * @param      mixed $values Criteria or Customer object containing data that is used to create the INSERT statement.
	 * @param      PropelPDO $con the PropelPDO connection to use
	 * @return     mixed The new primary key.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doInsert($values, PropelPDO $con = null)
	{
    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseCustomerPeer:doInsert:pre') as $sf_hook)
    {
      if (false !== $sf_hook_retval = call_user_func($sf_hook, 'BaseCustomerPeer', $values, $con))
      {
        return $sf_hook_retval;
      }
    }

		if ($con === null) {
			$con = Propel::getConnection(CustomerPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity
		} else {
			$criteria = $values->buildCriteria(); // build Criteria from Customer object
		}

		if ($criteria->containsKey(CustomerPeer::ID) && $criteria->keyContainsValue(CustomerPeer::ID) ) {
			throw new PropelException('Cannot insert a value for auto-increment primary key ('.CustomerPeer::ID.')');
		}


		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		try {
			// use transaction because $criteria could contain info
			// for more than one table (I guess, conceivably)
			$con->beginTransaction();
			$pk = BasePeer::doInsert($criteria, $con);
			$con->commit();
		} catch(PropelException $e) {
			$con->rollBack();
			throw $e;
		}

    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseCustomerPeer:doInsert:post') as $sf_hook)
    {
      call_user_func($sf_hook, 'BaseCustomerPeer', $values, $con, $pk);
    }

		return $pk;
	}

	/**
	 * Method perform an UPDATE on the database, given a Customer or Criteria object.
	 *
	 * @param      mixed $values Criteria or Customer object containing data that is used to create the UPDATE statement.
	 * @param      PropelPDO $con The connection to use (specify PropelPDO connection object to exert more control over transactions).
	 * @return     int The number of affected rows (if supported by underlying database driver).
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doUpdate($values, PropelPDO $con = null)
	{
    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseCustomerPeer:doUpdate:pre') as $sf_hook)
    {
      if (false !== $sf_hook_retval = call_user_func($sf_hook, 'BaseCustomerPeer', $values, $con))
      {
        return $sf_hook_retval;
      }
    }

		if ($con === null) {
			$con = Propel::getConnection(CustomerPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		$selectCriteria = new Criteria(self::DATABASE_NAME);

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity

			$comparison = $criteria->getComparison(CustomerPeer::ID);
			$selectCriteria->add(CustomerPeer::ID, $criteria->remove(CustomerPeer::ID), $comparison);

		} else { // $values is Customer object
			$criteria = $values->buildCriteria(); // gets full criteria
			$selectCriteria = $values->buildPkeyCriteria(); // gets criteria w/ primary key(s)
		}

		// set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		$ret = BasePeer::doUpdate($selectCriteria, $criteria, $con);

    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseCustomerPeer:doUpdate:post') as $sf_hook)
    {
      call_user_func($sf_hook, 'BaseCustomerPeer', $values, $con, $ret);
    }

    return $ret;
	}

	/**
	 * Method to DELETE all rows from the customer table.
	 *
	 * @return     int The number of affected rows (if supported by underlying database driver).
	 */
	public static function doDeleteAll($con = null)
	{
		if ($con === null) {
			$con = Propel::getConnection(CustomerPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}
		$affectedRows = 0; // initialize var to track total num of affected rows
		try {
			// use transaction because $criteria could contain info
			// for more than one table or we could emulating ON DELETE CASCADE, etc.
			$con->beginTransaction();
			$affectedRows += CustomerPeer::doOnDeleteCascade(new Criteria(CustomerPeer::DATABASE_NAME), $con);
			CustomerPeer::doOnDeleteSetNull(new Criteria(CustomerPeer::DATABASE_NAME), $con);
			$affectedRows += BasePeer::doDeleteAll(CustomerPeer::TABLE_NAME, $con);
			// Because this db requires some delete cascade/set null emulation, we have to
			// clear the cached instance *after* the emulation has happened (since
			// instances get re-added by the select statement contained therein).
			CustomerPeer::clearInstancePool();
			CustomerPeer::clearRelatedInstancePool();
			$con->commit();
			return $affectedRows;
		} catch (PropelException $e) {
			$con->rollBack();
			throw $e;
		}
	}

	/**
	 * Method perform a DELETE on the database, given a Customer or Criteria object OR a primary key value.
	 *
	 * @param      mixed $values Criteria or Customer object or primary key or array of primary keys
	 *              which is used to create the DELETE statement
	 * @param      PropelPDO $con the connection to use
	 * @return     int 	The number of affected rows (if supported by underlying database driver).  This includes CASCADE-related rows
	 *				if supported by native driver or if emulated using Propel.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	 public static function doDelete($values, PropelPDO $con = null)
	 {
		if ($con === null) {
			$con = Propel::getConnection(CustomerPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			// rename for clarity
			$criteria = clone $values;
		} elseif ($values instanceof Customer) { // it's a model object
			// create criteria based on pk values
			$criteria = $values->buildPkeyCriteria();
		} else { // it's a primary key, or an array of pks
			$criteria = new Criteria(self::DATABASE_NAME);
			$criteria->add(CustomerPeer::ID, (array) $values, Criteria::IN);
		}

		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		$affectedRows = 0; // initialize var to track total num of affected rows

		try {
			// use transaction because $criteria could contain info
			// for more than one table or we could emulating ON DELETE CASCADE, etc.
			$con->beginTransaction();
			$affectedRows += CustomerPeer::doOnDeleteCascade($criteria, $con);
			CustomerPeer::doOnDeleteSetNull($criteria, $con);
			
			// Because this db requires some delete cascade/set null emulation, we have to
			// clear the cached instance *after* the emulation has happened (since
			// instances get re-added by the select statement contained therein).
			if ($values instanceof Criteria) {
				CustomerPeer::clearInstancePool();
			} elseif ($values instanceof Customer) { // it's a model object
				CustomerPeer::removeInstanceFromPool($values);
			} else { // it's a primary key, or an array of pks
				foreach ((array) $values as $singleval) {
					CustomerPeer::removeInstanceFromPool($singleval);
				}
			}
			
			$affectedRows += BasePeer::doDelete($criteria, $con);
			CustomerPeer::clearRelatedInstancePool();
			$con->commit();
			return $affectedRows;
		} catch (PropelException $e) {
			$con->rollBack();
			throw $e;
		}
	}

	/**
	 * This is a method for emulating ON DELETE CASCADE for DBs that don't support this
	 * feature (like MySQL or SQLite).
	 *
	 * This method is not very speedy because it must perform a query first to get
	 * the implicated records and then perform the deletes by calling those Peer classes.
	 *
	 * This method should be used within a transaction if possible.
	 *
	 * @param      Criteria $criteria
	 * @param      PropelPDO $con
	 * @return     int The number of affected rows (if supported by underlying database driver).
	 */
	protected static function doOnDeleteCascade(Criteria $criteria, PropelPDO $con)
	{
		// initialize var to track total num of affected rows
		$affectedRows = 0;

		// first find the objects that are implicated by the $criteria
		$objects = CustomerPeer::doSelect($criteria, $con);
		foreach ($objects as $obj) {


			// delete related Groupe objects
			$criteria = new Criteria(GroupePeer::DATABASE_NAME);
			
			$criteria->add(GroupePeer::CUSTOMER_ID, $obj->getId());
			$affectedRows += GroupePeer::doDelete($criteria, $con);

			// delete related Log objects
			$criteria = new Criteria(LogPeer::DATABASE_NAME);
			
			$criteria->add(LogPeer::CUSTOMER_ID, $obj->getId());
			$affectedRows += LogPeer::doDelete($criteria, $con);

			// delete related Tag objects
			$criteria = new Criteria(TagPeer::DATABASE_NAME);
			
			$criteria->add(TagPeer::CUSTOMER_ID, $obj->getId());
			$affectedRows += TagPeer::doDelete($criteria, $con);

			// delete related Unit objects
			$criteria = new Criteria(UnitPeer::DATABASE_NAME);
			
			$criteria->add(UnitPeer::CUSTOMER_ID, $obj->getId());
			$affectedRows += UnitPeer::doDelete($criteria, $con);

			// delete related User objects
			$criteria = new Criteria(UserPeer::DATABASE_NAME);
			
			$criteria->add(UserPeer::CUSTOMER_ID, $obj->getId());
			$affectedRows += UserPeer::doDelete($criteria, $con);

			// delete related CustomerHasModule objects
			$criteria = new Criteria(CustomerHasModulePeer::DATABASE_NAME);
			
			$criteria->add(CustomerHasModulePeer::CUSTOMER_ID, $obj->getId());
			$affectedRows += CustomerHasModulePeer::doDelete($criteria, $con);

			// delete related UsageSupport objects
			$criteria = new Criteria(UsageSupportPeer::DATABASE_NAME);
			
			$criteria->add(UsageSupportPeer::CUSTOMER_ID, $obj->getId());
			$affectedRows += UsageSupportPeer::doDelete($criteria, $con);

			// delete related Thesaurus objects
			$criteria = new Criteria(ThesaurusPeer::DATABASE_NAME);
			
			$criteria->add(ThesaurusPeer::CUSTOMER_ID, $obj->getId());
			$affectedRows += ThesaurusPeer::doDelete($criteria, $con);

			// delete related Preset objects
			$criteria = new Criteria(PresetPeer::DATABASE_NAME);
			
			$criteria->add(PresetPeer::CUSTOMER_ID, $obj->getId());
			$affectedRows += PresetPeer::doDelete($criteria, $con);
		}
		return $affectedRows;
	}

	/**
	 * This is a method for emulating ON DELETE SET NULL DBs that don't support this
	 * feature (like MySQL or SQLite).
	 *
	 * This method is not very speedy because it must perform a query first to get
	 * the implicated records and then perform the deletes by calling those Peer classes.
	 *
	 * This method should be used within a transaction if possible.
	 *
	 * @param      Criteria $criteria
	 * @param      PropelPDO $con
	 * @return     void
	 */
	protected static function doOnDeleteSetNull(Criteria $criteria, PropelPDO $con)
	{

		// first find the objects that are implicated by the $criteria
		$objects = CustomerPeer::doSelect($criteria, $con);
		foreach ($objects as $obj) {

			// set fkey col in related Disk rows to NULL
			$selectCriteria = new Criteria(CustomerPeer::DATABASE_NAME);
			$updateValues = new Criteria(CustomerPeer::DATABASE_NAME);
			$selectCriteria->add(DiskPeer::CUSTOMER_ID, $obj->getId());
			$updateValues->add(DiskPeer::CUSTOMER_ID, null);

					BasePeer::doUpdate($selectCriteria, $updateValues, $con); // use BasePeer because generated Peer doUpdate() methods only update using pkey

		}
	}

	/**
	 * Validates all modified columns of given Customer object.
	 * If parameter $columns is either a single column name or an array of column names
	 * than only those columns are validated.
	 *
	 * NOTICE: This does not apply to primary or foreign keys for now.
	 *
	 * @param      Customer $obj The object to validate.
	 * @param      mixed $cols Column name or array of column names.
	 *
	 * @return     mixed TRUE if all columns are valid or the error message of the first invalid column.
	 */
	public static function doValidate(Customer $obj, $cols = null)
	{
		$columns = array();

		if ($cols) {
			$dbMap = Propel::getDatabaseMap(CustomerPeer::DATABASE_NAME);
			$tableMap = $dbMap->getTable(CustomerPeer::TABLE_NAME);

			if (! is_array($cols)) {
				$cols = array($cols);
			}

			foreach ($cols as $colName) {
				if ($tableMap->containsColumn($colName)) {
					$get = 'get' . $tableMap->getColumn($colName)->getPhpName();
					$columns[$colName] = $obj->$get();
				}
			}
		} else {

		}

		return BasePeer::doValidate(CustomerPeer::DATABASE_NAME, CustomerPeer::TABLE_NAME, $columns);
	}

	/**
	 * Retrieve a single object by pkey.
	 *
	 * @param      int $pk the primary key.
	 * @param      PropelPDO $con the connection to use
	 * @return     Customer
	 */
	public static function retrieveByPK($pk, PropelPDO $con = null)
	{

		if (null !== ($obj = CustomerPeer::getInstanceFromPool((string) $pk))) {
			return $obj;
		}

		if ($con === null) {
			$con = Propel::getConnection(CustomerPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}

		$criteria = new Criteria(CustomerPeer::DATABASE_NAME);
		$criteria->add(CustomerPeer::ID, $pk);

		$v = CustomerPeer::doSelect($criteria, $con);

		return !empty($v) > 0 ? $v[0] : null;
	}

	/**
	 * Retrieve multiple objects by pkey.
	 *
	 * @param      array $pks List of primary keys
	 * @param      PropelPDO $con the connection to use
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function retrieveByPKs($pks, PropelPDO $con = null)
	{
		if ($con === null) {
			$con = Propel::getConnection(CustomerPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}

		$objs = null;
		if (empty($pks)) {
			$objs = array();
		} else {
			$criteria = new Criteria(CustomerPeer::DATABASE_NAME);
			$criteria->add(CustomerPeer::ID, $pks, Criteria::IN);
			$objs = CustomerPeer::doSelect($criteria, $con);
		}
		return $objs;
	}

