					$obj8 = UsageCommercialPeer::getInstanceFromPool($key8);
					if (!$obj8) {
	
						$cls = UsageCommercialPeer::getOMClass(false);

					$obj8 = new $cls();
					$obj8->hydrate($row, $startcol8);
					UsageCommercialPeer::addInstanceToPool($obj8, $key8);
				} // if $obj8 already loaded

				// Add the $obj1 (Folder) to the collection in $obj8 (UsageCommercial)
				$obj8->addFolder($obj1);

			} // if joined row is not null

				// Add objects for joined CreativeCommons rows

				$key9 = CreativeCommonsPeer::getPrimaryKeyHashFromRow($row, $startcol9);
				if ($key9 !== null) {
					$obj9 = CreativeCommonsPeer::getInstanceFromPool($key9);
					if (!$obj9) {
	
						$cls = CreativeCommonsPeer::getOMClass(false);

					$obj9 = new $cls();
					$obj9->hydrate($row, $startcol9);
					CreativeCommonsPeer::addInstanceToPool($obj9, $key9);
				} // if $obj9 already loaded

				// Add the $obj1 (Folder) to the collection in $obj9 (CreativeCommons)
				$obj9->addFolder($obj1);

			} // if joined row is not null

			$results[] = $obj1;
		}
		$stmt->closeCursor();
		return $results;
	}

