					}
				}
			}

			if ($this->collFolders !== null) {
				foreach ($this->collFolders as $referrerFK) {
					if (!$referrerFK->isDeleted()) {
						$affectedRows += $referrerFK->save($con);
					}
				}
			}

			if ($this->collUserGroups !== null) {
				foreach ($this->collUserGroups as $referrerFK) {
					if (!$referrerFK->isDeleted()) {
						$affectedRows += $referrerFK->save($con);
					}
				}
			}

			if ($this->collUnitGroups !== null) {
				foreach ($this->collUnitGroups as $referrerFK) {
					if (!$referrerFK->isDeleted()) {
						$affectedRows += $referrerFK->save($con);
					}
				}
			}

			if ($this->collGroupeConstraints !== null) {
				foreach ($this->collGroupeConstraints as $referrerFK) {
					if (!$referrerFK->isDeleted()) {
