			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return the collection.


				$criteria->add(LogGroupePeer::GROUPE_ID, $this->id);

				LogGroupePeer::addSelectColumns($criteria);
				if (!isset($this->lastLogGroupeCriteria) || !$this->lastLogGroupeCriteria->equals($criteria)) {
					$this->collLogGroupes = LogGroupePeer::doSelect($criteria, $con);
				}
			}
		}
		$this->lastLogGroupeCriteria = $criteria;
		return $this->collLogGroupes;
	}

	/**
	 * Returns the number of related LogGroupe objects.
	 *
	 * @param      Criteria $criteria
	 * @param      boolean $distinct
	 * @param      PropelPDO $con
	 * @return     int Count of related LogGroupe objects.
	 * @throws     PropelException
	 */
	public function countLogGroupes(Criteria $criteria = null, $distinct = false, PropelPDO $con = null)
	{
		if ($criteria === null) {
			$criteria = new Criteria(GroupePeer::DATABASE_NAME);
		} else {
			$criteria = clone $criteria;
		}

		if ($distinct) {
			$criteria->setDistinct();
		}

		$count = null;

		if ($this->collLogGroupes === null) {
			if ($this->isNew()) {
				$count = 0;
			} else {

				$criteria->add(LogGroupePeer::GROUPE_ID, $this->id);

				$count = LogGroupePeer::doCount($criteria, false, $con);
			}
		} else {
			// criteria has no effect for a new object
			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return count of the collection.


				$criteria->add(LogGroupePeer::GROUPE_ID, $this->id);

				if (!isset($this->lastLogGroupeCriteria) || !$this->lastLogGroupeCriteria->equals($criteria)) {
					$count = LogGroupePeer::doCount($criteria, false, $con);
				} else {
					$count = count($this->collLogGroupes);
				}
			} else {
				$count = count($this->collLogGroupes);
			}
		}
		return $count;
	}

	/**
	 * Method called to associate a LogGroupe object to this object
	 * through the LogGroupe foreign key attribute.
	 *
	 * @param      LogGroupe $l LogGroupe
	 * @return     void
	 * @throws     PropelException
	 */
	public function addLogGroupe(LogGroupe $l)
	{
		if ($this->collLogGroupes === null) {
			$this->initLogGroupes();
		}
		if (!in_array($l, $this->collLogGroupes, true)) { // only add it if the **same** object is not already associated
			array_push($this->collLogGroupes, $l);
			$l->setGroupe($this);
		}
	}

	/**
	 * Resets all collections of referencing foreign keys.
	 *
	 * This method is a user-space workaround for PHP's inability to garbage collect objects
	 * with circular references.  This is currently necessary when using Propel in certain
	 * daemon or large-volumne/high-memory operations.
	 *
	 * @param      boolean $deep Whether to also clear the references on all associated objects.
	 */
	public function clearAllReferences($deep = false)
	{
		if ($deep) {
			if ($this->collFields) {
				foreach ((array) $this->collFields as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collFiles) {
				foreach ((array) $this->collFiles as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collFolders) {
				foreach ((array) $this->collFolders as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collUserGroups) {
				foreach ((array) $this->collUserGroups as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collUnitGroups) {
				foreach ((array) $this->collUnitGroups as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collGroupeConstraints) {
				foreach ((array) $this->collGroupeConstraints as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collLogGroupes) {
				foreach ((array) $this->collLogGroupes as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collFields = null;
		$this->collFiles = null;
		$this->collFolders = null;
		$this->collUserGroups = null;
		$this->collUnitGroups = null;
		$this->collGroupeConstraints = null;
		$this->collLogGroupes = null;
			$this->aUser = null;
			$this->aCustomer = null;
			$this->aDisk = null;
			$this->aRole = null;
	}

	// symfony_behaviors behavior
	
	/**
	 * Calls methods defined via {@link sfMixer}.
	 */
	public function __call($method, $arguments)
	{
	  if (!$callable = sfMixer::getCallable('BaseGroupe:'.$method))
	  {
	    throw new sfException(sprintf('Call to undefined method BaseGroupe::%s', $method));
	  }
	
	  array_unshift($arguments, $this);
	
	  return call_user_func_array($callable, $arguments);
	}

