	 *
	 * You can specify the key type of the array by additionally passing one
	 * of the class type constants BasePeer::TYPE_PHPNAME, BasePeer::TYPE_STUDLYPHPNAME,
	 * BasePeer::TYPE_COLNAME, BasePeer::TYPE_FIELDNAME, BasePeer::TYPE_NUM.
	 * The default key type is the column's phpname (e.g. 'AuthorId')
	 *
	 * @param      array  $arr     An array to populate the object from.
	 * @param      string $keyType The type of keys the array uses.
	 * @return     void
	 */
	public function fromArray($arr, $keyType = BasePeer::TYPE_PHPNAME)
	{
		$keys = LogGroupePeer::getFieldNames($keyType);

		if (array_key_exists($keys[0], $arr)) $this->setId($arr[$keys[0]]);
		if (array_key_exists($keys[1], $arr)) $this->setGroupeId($arr[$keys[1]]);
		if (array_key_exists($keys[2], $arr)) $this->setUsedSpaceDisk($arr[$keys[2]]);
		if (array_key_exists($keys[3], $arr)) $this->setFolders($arr[$keys[3]]);
		if (array_key_exists($keys[4], $arr)) $this->setFiles($arr[$keys[4]]);
		if (array_key_exists($keys[5], $arr)) $this->setUploadTraffic($arr[$keys[5]]);
		if (array_key_exists($keys[6], $arr)) $this->setDownloadTraffic($arr[$keys[6]]);
		if (array_key_exists($keys[7], $arr)) $this->setUploadTrafficFiles($arr[$keys[7]]);
		if (array_key_exists($keys[8], $arr)) $this->setDownloadTrafficFiles($arr[$keys[8]]);
		if (array_key_exists($keys[9], $arr)) $this->setViews($arr[$keys[9]]);
		if (array_key_exists($keys[10], $arr)) $this->setUniqueViews($arr[$keys[10]]);
		if (array_key_exists($keys[11], $arr)) $this->setCreatedAt($arr[$keys[11]]);
