	/**
	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(LogGroupePeer::DATABASE_NAME);

		if ($this->isColumnModified(LogGroupePeer::ID)) $criteria->add(LogGroupePeer::ID, $this->id);
		if ($this->isColumnModified(LogGroupePeer::GROUPE_ID)) $criteria->add(LogGroupePeer::GROUPE_ID, $this->groupe_id);
		if ($this->isColumnModified(LogGroupePeer::USED_SPACE_DISK)) $criteria->add(LogGroupePeer::USED_SPACE_DISK, $this->used_space_disk);
		if ($this->isColumnModified(LogGroupePeer::FOLDERS)) $criteria->add(LogGroupePeer::FOLDERS, $this->folders);
		if ($this->isColumnModified(LogGroupePeer::FILES)) $criteria->add(LogGroupePeer::FILES, $this->files);
		if ($this->isColumnModified(LogGroupePeer::UPLOAD_TRAFFIC)) $criteria->add(LogGroupePeer::UPLOAD_TRAFFIC, $this->upload_traffic);
		if ($this->isColumnModified(LogGroupePeer::DOWNLOAD_TRAFFIC)) $criteria->add(LogGroupePeer::DOWNLOAD_TRAFFIC, $this->download_traffic);
		if ($this->isColumnModified(LogGroupePeer::UPLOAD_TRAFFIC_FILES)) $criteria->add(LogGroupePeer::UPLOAD_TRAFFIC_FILES, $this->upload_traffic_files);
		if ($this->isColumnModified(LogGroupePeer::DOWNLOAD_TRAFFIC_FILES)) $criteria->add(LogGroupePeer::DOWNLOAD_TRAFFIC_FILES, $this->download_traffic_files);
		if ($this->isColumnModified(LogGroupePeer::VIEWS)) $criteria->add(LogGroupePeer::VIEWS, $this->views);
		if ($this->isColumnModified(LogGroupePeer::UNIQUE_VIEWS)) $criteria->add(LogGroupePeer::UNIQUE_VIEWS, $this->unique_views);
		if ($this->isColumnModified(LogGroupePeer::CREATED_AT)) $criteria->add(LogGroupePeer::CREATED_AT, $this->created_at);

		return $criteria;
	}

	/**
	 * Builds a Criteria object containing the primary key for this object.
	 *
	 * Unlike buildCriteria() this method includes the primary key values regardless
	 * of whether or not they have been modified.
	 *
	 * @return     Criteria The Criteria object containing value(s) for primary key(s).
