	// symfony_behaviors behavior
	
	/**
	 * Calls methods defined via {@link sfMixer}.
	 */
	public function __call($method, $arguments)
	{
	  if (!$callable = sfMixer::getCallable('BaseModuleValue:'.$method))
	  {
	    throw new sfException(sprintf('Call to undefined method BaseModuleValue::%s', $method));
	  }
	
	  array_unshift($arguments, $this);
	
	  return call_user_func_array($callable, $arguments);
	}

	// symfony_i18n behavior
	
	/**
	 * Returns the culture.
	 *
	 * @return string The culture
	 */
	public function getCulture()
	{
	  return $this->culture;
	}
	
	/**
	 * Sets the culture.
	 *
	 * @param string  The culture to set
	 *
	 * @return ModuleValue
	 */
	public function setCulture($culture)
	{
	  $this->culture = $culture;
	  return $this;
	}
	
	/**
	 * Returns the "description" value from the current {@link ModuleValueI18n}.
	 */
	public function getDescription($culture = null)
	{
	  return $this->getCurrentModuleValueI18n($culture)->getDescription();
	}
	
	/**
	 * Sets the "description" value of the current {@link ModuleValueI18n}.
	 *
	 * @return ModuleValue
	 */
	public function setDescription($value, $culture = null)
	{
	  $this->getCurrentModuleValueI18n($culture)->setDescription($value);
	  return $this;
	}
	
	/**
	 * Returns the current translation.
	 *
	 * @return ModuleValueI18n
	 */
	public function getCurrentModuleValueI18n($culture = null)
	{
	  if (null === $culture)
	  {
	    $culture = null === $this->culture ? sfPropel::getDefaultCulture() : $this->culture;
	  }
	
	  if (!isset($this->current_i18n[$culture]))
	  {
	    $object = $this->isNew() ? null : ModuleValueI18nPeer::retrieveByPK($this->getPrimaryKey(), $culture);
	    if ($object)
	    {
	      $this->setModuleValueI18nForCulture($object, $culture);
	    }
	    else
	    {
	      $this->setModuleValueI18nForCulture(new ModuleValueI18n(), $culture);
	      $this->current_i18n[$culture]->setCulture($culture);
	    }
	  }
	
	  return $this->current_i18n[$culture];
	}
	
	/**
	 * Sets the translation object for a culture.
	 */
	public function setModuleValueI18nForCulture(ModuleValueI18n $object, $culture)
	{
	  $this->current_i18n[$culture] = $object;
	  $this->addModuleValueI18n($object);
	}

