				foreach (sfMixer::getCallables('BasePermalink:save:post') as $callable)
				{
				  call_user_func($callable, $this, $con, $affectedRows);
				}

				PermalinkPeer::addInstanceToPool($this);
			} else {
				$affectedRows = 0;
			}
			$con->commit();
			return $affectedRows;
		} catch (PropelException $e) {
			$con->rollBack();
			throw $e;
		}
