	public static function doSelectJoinAllExceptCreativeCommons(Criteria $criteria, $con = null, $join_behavior = Criteria::LEFT_JOIN)
	{
		$criteria = clone $criteria;

		// Set the correct dbName if it has not been overridden
		// $criteria->getDbName() will return the same object if not set to another value
		// so == check is okay and faster
		if ($criteria->getDbName() == Propel::getDefaultDB()) {
			$criteria->setDbName(self::DATABASE_NAME);
		}

		PresetPeer::addSelectColumns($criteria);
		$startcol2 = (PresetPeer::NUM_COLUMNS - PresetPeer::NUM_LAZY_LOAD_COLUMNS);

		CustomerPeer::addSelectColumns($criteria);
		$startcol3 = $startcol2 + (CustomerPeer::NUM_COLUMNS - CustomerPeer::NUM_LAZY_LOAD_COLUMNS);

		LicencePeer::addSelectColumns($criteria);
