		$criteria->addJoin(PresetPeer::CUSTOMER_ID, CustomerPeer::ID, $join_behavior);

		$criteria->addJoin(PresetPeer::LICENCE_ID, LicencePeer::ID, $join_behavior);

		$criteria->addJoin(PresetPeer::USAGE_DISTRIBUTION_ID, UsageDistributionPeer::ID, $join_behavior);

		$criteria->addJoin(PresetPeer::USAGE_CONSTRAINT_ID, UsageConstraintPeer::ID, $join_behavior);

