					$obj3->hydrate($row, $startcol3);
					LicencePeer::addInstanceToPool($obj3, $key3);
				} // if $obj3 already loaded

				// Add the $obj1 (Preset) to the collection in $obj3 (Licence)
				$obj3->addPreset($obj1);

			} // if joined row is not null

				// Add objects for joined UsageDistribution rows

				$key4 = UsageDistributionPeer::getPrimaryKeyHashFromRow($row, $startcol4);
				if ($key4 !== null) {
					$obj4 = UsageDistributionPeer::getInstanceFromPool($key4);
					if (!$obj4) {
	
						$cls = UsageDistributionPeer::getOMClass(false);

					$obj4 = new $cls();
					$obj4->hydrate($row, $startcol4);
					UsageDistributionPeer::addInstanceToPool($obj4, $key4);
				} // if $obj4 already loaded

				// Add the $obj1 (Preset) to the collection in $obj4 (UsageDistribution)
				$obj4->addPreset($obj1);

			} // if joined row is not null

				// Add objects for joined UsageConstraint rows

				$key5 = UsageConstraintPeer::getPrimaryKeyHashFromRow($row, $startcol5);
				if ($key5 !== null) {
					$obj5 = UsageConstraintPeer::getInstanceFromPool($key5);
