	/**
	 * Method perform an INSERT on the database, given a User or Criteria object.
	 *
	 * @param      mixed $values Criteria or User object containing data that is used to create the INSERT statement.
	 * @param      PropelPDO $con the PropelPDO connection to use
	 * @return     mixed The new primary key.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doInsert($values, PropelPDO $con = null)
	{
    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseUserPeer:doInsert:pre') as $sf_hook)
    {
      if (false !== $sf_hook_retval = call_user_func($sf_hook, 'BaseUserPeer', $values, $con))
      {
        return $sf_hook_retval;
      }
    }

		if ($con === null) {
			$con = Propel::getConnection(UserPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity
		} else {
			$criteria = $values->buildCriteria(); // build Criteria from User object
		}

		if ($criteria->containsKey(UserPeer::ID) && $criteria->keyContainsValue(UserPeer::ID) ) {
			throw new PropelException('Cannot insert a value for auto-increment primary key ('.UserPeer::ID.')');
		}


		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		try {
			// use transaction because $criteria could contain info
			// for more than one table (I guess, conceivably)
			$con->beginTransaction();
			$pk = BasePeer::doInsert($criteria, $con);
			$con->commit();
		} catch(PropelException $e) {
			$con->rollBack();
			throw $e;
		}

    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseUserPeer:doInsert:post') as $sf_hook)
    {
      call_user_func($sf_hook, 'BaseUserPeer', $values, $con, $pk);
    }

		return $pk;
	}

	/**
	 * Method perform an UPDATE on the database, given a User or Criteria object.
	 *
	 * @param      mixed $values Criteria or User object containing data that is used to create the UPDATE statement.
	 * @param      PropelPDO $con The connection to use (specify PropelPDO connection object to exert more control over transactions).
	 * @return     int The number of affected rows (if supported by underlying database driver).
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doUpdate($values, PropelPDO $con = null)
	{
    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseUserPeer:doUpdate:pre') as $sf_hook)
    {
      if (false !== $sf_hook_retval = call_user_func($sf_hook, 'BaseUserPeer', $values, $con))
      {
        return $sf_hook_retval;
      }
    }

		if ($con === null) {
			$con = Propel::getConnection(UserPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		$selectCriteria = new Criteria(self::DATABASE_NAME);

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity

			$comparison = $criteria->getComparison(UserPeer::ID);
			$selectCriteria->add(UserPeer::ID, $criteria->remove(UserPeer::ID), $comparison);

		} else { // $values is User object
			$criteria = $values->buildCriteria(); // gets full criteria
			$selectCriteria = $values->buildPkeyCriteria(); // gets criteria w/ primary key(s)
		}

		// set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		$ret = BasePeer::doUpdate($selectCriteria, $criteria, $con);

    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseUserPeer:doUpdate:post') as $sf_hook)
    {
      call_user_func($sf_hook, 'BaseUserPeer', $values, $con, $ret);
    }

    return $ret;
	}

	/**
	 * Method to DELETE all rows from the user table.
	 *
	 * @return     int The number of affected rows (if supported by underlying database driver).
	 */
	public static function doDeleteAll($con = null)
	{
		if ($con === null) {
			$con = Propel::getConnection(UserPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}
		$affectedRows = 0; // initialize var to track total num of affected rows
		try {
			// use transaction because $criteria could contain info
			// for more than one table or we could emulating ON DELETE CASCADE, etc.
			$con->beginTransaction();
			$affectedRows += UserPeer::doOnDeleteCascade(new Criteria(UserPeer::DATABASE_NAME), $con);
			UserPeer::doOnDeleteSetNull(new Criteria(UserPeer::DATABASE_NAME), $con);
			$affectedRows += BasePeer::doDeleteAll(UserPeer::TABLE_NAME, $con);
			// Because this db requires some delete cascade/set null emulation, we have to
			// clear the cached instance *after* the emulation has happened (since
			// instances get re-added by the select statement contained therein).
			UserPeer::clearInstancePool();
			UserPeer::clearRelatedInstancePool();
			$con->commit();
			return $affectedRows;
		} catch (PropelException $e) {
			$con->rollBack();
			throw $e;
		}
	}

	/**
	 * Method perform a DELETE on the database, given a User or Criteria object OR a primary key value.
	 *
	 * @param      mixed $values Criteria or User object or primary key or array of primary keys
	 *              which is used to create the DELETE statement
	 * @param      PropelPDO $con the connection to use
	 * @return     int 	The number of affected rows (if supported by underlying database driver).  This includes CASCADE-related rows
	 *				if supported by native driver or if emulated using Propel.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	 public static function doDelete($values, PropelPDO $con = null)
	 {
		if ($con === null) {
			$con = Propel::getConnection(UserPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			// rename for clarity
			$criteria = clone $values;
		} elseif ($values instanceof User) { // it's a model object
			// create criteria based on pk values
			$criteria = $values->buildPkeyCriteria();
		} else { // it's a primary key, or an array of pks
			$criteria = new Criteria(self::DATABASE_NAME);
			$criteria->add(UserPeer::ID, (array) $values, Criteria::IN);
		}

		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		$affectedRows = 0; // initialize var to track total num of affected rows

		try {
			// use transaction because $criteria could contain info
			// for more than one table or we could emulating ON DELETE CASCADE, etc.
			$con->beginTransaction();
			$affectedRows += UserPeer::doOnDeleteCascade($criteria, $con);
			UserPeer::doOnDeleteSetNull($criteria, $con);
			
			// Because this db requires some delete cascade/set null emulation, we have to
			// clear the cached instance *after* the emulation has happened (since
			// instances get re-added by the select statement contained therein).
			if ($values instanceof Criteria) {
				UserPeer::clearInstancePool();
			} elseif ($values instanceof User) { // it's a model object
				UserPeer::removeInstanceFromPool($values);
			} else { // it's a primary key, or an array of pks
				foreach ((array) $values as $singleval) {
					UserPeer::removeInstanceFromPool($singleval);
				}
			}
			
			$affectedRows += BasePeer::doDelete($criteria, $con);
			UserPeer::clearRelatedInstancePool();
			$con->commit();
			return $affectedRows;
		} catch (PropelException $e) {
			$con->rollBack();
			throw $e;
		}
	}

	/**
	 * This is a method for emulating ON DELETE CASCADE for DBs that don't support this
	 * feature (like MySQL or SQLite).
	 *
	 * This method is not very speedy because it must perform a query first to get
	 * the implicated records and then perform the deletes by calling those Peer classes.
	 *
	 * This method should be used within a transaction if possible.
	 *
	 * @param      Criteria $criteria
	 * @param      PropelPDO $con
	 * @return     int The number of affected rows (if supported by underlying database driver).
	 */
	protected static function doOnDeleteCascade(Criteria $criteria, PropelPDO $con)
	{
		// initialize var to track total num of affected rows
		$affectedRows = 0;

		// first find the objects that are implicated by the $criteria
		$objects = UserPeer::doSelect($criteria, $con);
		foreach ($objects as $obj) {


			// delete related Comment objects
			$criteria = new Criteria(CommentPeer::DATABASE_NAME);
			
			$criteria->add(CommentPeer::USER_ID, $obj->getId());
			$affectedRows += CommentPeer::doDelete($criteria, $con);

			// delete related Favorites objects
			$criteria = new Criteria(FavoritesPeer::DATABASE_NAME);
			
			$criteria->add(FavoritesPeer::USER_ID, $obj->getId());
			$affectedRows += FavoritesPeer::doDelete($criteria, $con);

			// delete related File objects
			$criteria = new Criteria(FilePeer::DATABASE_NAME);
			
			$criteria->add(FilePeer::USER_ID, $obj->getId());
			$affectedRows += FilePeer::doDelete($criteria, $con);

			// delete related FileTmp objects
			$criteria = new Criteria(FileTmpPeer::DATABASE_NAME);
			
			$criteria->add(FileTmpPeer::USER_ID, $obj->getId());
			$affectedRows += FileTmpPeer::doDelete($criteria, $con);

			// delete related Folder objects
			$criteria = new Criteria(FolderPeer::DATABASE_NAME);
			
			$criteria->add(FolderPeer::USER_ID, $obj->getId());
			$affectedRows += FolderPeer::doDelete($criteria, $con);

			// delete related Groupe objects
			$criteria = new Criteria(GroupePeer::DATABASE_NAME);
			
			$criteria->add(GroupePeer::USER_ID, $obj->getId());
			$affectedRows += GroupePeer::doDelete($criteria, $con);

			// delete related Log objects
			$criteria = new Criteria(LogPeer::DATABASE_NAME);
			
			$criteria->add(LogPeer::USER_ID, $obj->getId());
			$affectedRows += LogPeer::doDelete($criteria, $con);

			// delete related Request objects
			$criteria = new Criteria(RequestPeer::DATABASE_NAME);
			
			$criteria->add(RequestPeer::USER_ID, $obj->getId());
			$affectedRows += RequestPeer::doDelete($criteria, $con);

			// delete related UserFolder objects
			$criteria = new Criteria(UserFolderPeer::DATABASE_NAME);
			
			$criteria->add(UserFolderPeer::USER_ID, $obj->getId());
			$affectedRows += UserFolderPeer::doDelete($criteria, $con);

			// delete related UserGroup objects
			$criteria = new Criteria(UserGroupPeer::DATABASE_NAME);
			
			$criteria->add(UserGroupPeer::USER_ID, $obj->getId());
			$affectedRows += UserGroupPeer::doDelete($criteria, $con);

			// delete related UserUnit objects
			$criteria = new Criteria(UserUnitPeer::DATABASE_NAME);
			
			$criteria->add(UserUnitPeer::USER_ID, $obj->getId());
			$affectedRows += UserUnitPeer::doDelete($criteria, $con);

			// delete related UserPreference objects
			$criteria = new Criteria(UserPreferencePeer::DATABASE_NAME);
			
			$criteria->add(UserPreferencePeer::USER_ID, $obj->getId());
			$affectedRows += UserPreferencePeer::doDelete($criteria, $con);

			// delete related Basket objects
			$criteria = new Criteria(BasketPeer::DATABASE_NAME);
			
			$criteria->add(BasketPeer::USER_ID, $obj->getId());
			$affectedRows += BasketPeer::doDelete($criteria, $con);

			// delete related FileWaiting objects
			$criteria = new Criteria(FileWaitingPeer::DATABASE_NAME);
			
			$criteria->add(FileWaitingPeer::USER_ID, $obj->getId());
			$affectedRows += FileWaitingPeer::doDelete($criteria, $con);

			// delete related FileRelated objects
			$criteria = new Criteria(FileRelatedPeer::DATABASE_NAME);
			
			$criteria->add(FileRelatedPeer::USER_ID, $obj->getId());
			$affectedRows += FileRelatedPeer::doDelete($criteria, $con);

			// delete related LogUser objects
			$criteria = new Criteria(LogUserPeer::DATABASE_NAME);
			
			$criteria->add(LogUserPeer::USER_ID, $obj->getId());
			$affectedRows += LogUserPeer::doDelete($criteria, $con);

			// delete related PermalinkNotification objects
			$criteria = new Criteria(PermalinkNotificationPeer::DATABASE_NAME);
			
			$criteria->add(PermalinkNotificationPeer::USER_ID, $obj->getId());
			$affectedRows += PermalinkNotificationPeer::doDelete($criteria, $con);

			// delete related PermalinkNotification objects
			$criteria = new Criteria(PermalinkNotificationPeer::DATABASE_NAME);
			
			$criteria->add(PermalinkNotificationPeer::PERMALINK_ID, $obj->getId());
			$affectedRows += PermalinkNotificationPeer::doDelete($criteria, $con);

			// delete related UserHasModule objects
			$criteria = new Criteria(UserHasModulePeer::DATABASE_NAME);
			
			$criteria->add(UserHasModulePeer::USER_ID, $obj->getId());
			$affectedRows += UserHasModulePeer::doDelete($criteria, $con);

			// delete related BasketRequest objects
			$criteria = new Criteria(BasketRequestPeer::DATABASE_NAME);
			
			$criteria->add(BasketRequestPeer::USER_ID, $obj->getId());
			$affectedRows += BasketRequestPeer::doDelete($criteria, $con);

			// delete related ResetPasswordRequest objects
			$criteria = new Criteria(ResetPasswordRequestPeer::DATABASE_NAME);
			
			$criteria->add(ResetPasswordRequestPeer::USER_ID, $obj->getId());
			$affectedRows += ResetPasswordRequestPeer::doDelete($criteria, $con);
		}
		return $affectedRows;
	}

	/**
	 * This is a method for emulating ON DELETE SET NULL DBs that don't support this
	 * feature (like MySQL or SQLite).
	 *
	 * This method is not very speedy because it must perform a query first to get
	 * the implicated records and then perform the deletes by calling those Peer classes.
	 *
	 * This method should be used within a transaction if possible.
	 *
	 * @param      Criteria $criteria
	 * @param      PropelPDO $con
	 * @return     void
	 */
	protected static function doOnDeleteSetNull(Criteria $criteria, PropelPDO $con)
	{

		// first find the objects that are implicated by the $criteria
		$objects = UserPeer::doSelect($criteria, $con);
		foreach ($objects as $obj) {

			// set fkey col in related UniqueKey rows to NULL
			$selectCriteria = new Criteria(UserPeer::DATABASE_NAME);
			$updateValues = new Criteria(UserPeer::DATABASE_NAME);
			$selectCriteria->add(UniqueKeyPeer::USER_ID, $obj->getId());
			$updateValues->add(UniqueKeyPeer::USER_ID, null);

					BasePeer::doUpdate($selectCriteria, $updateValues, $con); // use BasePeer because generated Peer doUpdate() methods only update using pkey

		}
	}

	/**
	 * Validates all modified columns of given User object.
	 * If parameter $columns is either a single column name or an array of column names
	 * than only those columns are validated.
	 *
	 * NOTICE: This does not apply to primary or foreign keys for now.
	 *
	 * @param      User $obj The object to validate.
	 * @param      mixed $cols Column name or array of column names.
	 *
	 * @return     mixed TRUE if all columns are valid or the error message of the first invalid column.
	 */
	public static function doValidate(User $obj, $cols = null)
	{
		$columns = array();

		if ($cols) {
			$dbMap = Propel::getDatabaseMap(UserPeer::DATABASE_NAME);
			$tableMap = $dbMap->getTable(UserPeer::TABLE_NAME);

			if (! is_array($cols)) {
				$cols = array($cols);
			}

			foreach ($cols as $colName) {
				if ($tableMap->containsColumn($colName)) {
					$get = 'get' . $tableMap->getColumn($colName)->getPhpName();
					$columns[$colName] = $obj->$get();
				}
			}
		} else {

		}

		return BasePeer::doValidate(UserPeer::DATABASE_NAME, UserPeer::TABLE_NAME, $columns);
	}

	/**
	 * Retrieve a single object by pkey.
	 *
	 * @param      int $pk the primary key.
	 * @param      PropelPDO $con the connection to use
	 * @return     User
	 */
	public static function retrieveByPK($pk, PropelPDO $con = null)
	{

		if (null !== ($obj = UserPeer::getInstanceFromPool((string) $pk))) {
			return $obj;
		}

		if ($con === null) {
			$con = Propel::getConnection(UserPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}

		$criteria = new Criteria(UserPeer::DATABASE_NAME);
		$criteria->add(UserPeer::ID, $pk);

		$v = UserPeer::doSelect($criteria, $con);

		return !empty($v) > 0 ? $v[0] : null;
	}

	/**
	 * Retrieve multiple objects by pkey.
	 *
	 * @param      array $pks List of primary keys
	 * @param      PropelPDO $con the connection to use
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function retrieveByPKs($pks, PropelPDO $con = null)
	{
		if ($con === null) {
			$con = Propel::getConnection(UserPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}

		$objs = null;
		if (empty($pks)) {
			$objs = array();
		} else {
			$criteria = new Criteria(UserPeer::DATABASE_NAME);
			$criteria->add(UserPeer::ID, $pks, Criteria::IN);
			$objs = UserPeer::doSelect($criteria, $con);
		}
		return $objs;
	}

